<template>
<form class="form">
  <input v-model="text" placeholder="Search anime..." class="input">
  <div v-for="anime in searchResult" :key="anime.text" class="box">
    <img class="image-placeholder" src="data:image/jpeg;base64," alt="">
    <div class="info-column">
      <h1 class="title">{{anime.title}}</h1>
      <div class="info-row">
        <p class="rating">PG 13</p>
        <div>
          <p>9.99</p>
        </div>
        <p>Oct 20, 1999</p>
      </div>
    </div>
  </div>
  </form>
</template>

<script>
export default {
  data() {
    return {
      text: "",
      animes: [
        {title: "One Punch Man"},
        {title: "Spy x Family"},
        {title: "Chainsaw Man"},
        {title: "Summertime Render"},
        {title: "Is It Wrong To Try To Pick Up Girls In A Dungeon?"},
        ],
    }
  },
  computed: {
    searchResult() {
      if (this.text) {
        return this.animes.filter((anime) => {
          return this.text.toLowerCase().split().every(search => anime.title.toLowerCase().includes(search))
        })
      }
    }
  }
}
</script>

